import React, { useState, useEffect } from 'react'
import './VideoRoom.css'
import Sketch from 'react-p5';

function VideoRoom({ socket, name, room }) {
	const [videoGrid, setVideoGrid] = useState([]);
	const [myVideo, setMyVideo] = useState({});
	const [users, setUsers] = useState([]);

	useEffect(() => {
		let tempUsers = [];
		tempUsers.push({
			id: socket.id,
			room: room,
			name: name,
			x: 400,
			y: 100
		});
		setUsers(tempUsers);
	}, []);

	useEffect(() => {
		socket.on('receive-move', (data) => {
			let tempUsers = users;
			let idx = tempUsers.findIndex((user) => user.id === data.id)
			if (idx === -1) {
				tempUsers.push({
					id: data.id, 
					room: data.room,
					sender: data.sender,
					x: data.x,
					y: data.y	
				})
			} else {
				tempUsers[idx] = {
					id: data.id, 
					room: data.room,
					sender: data.sender,
					x: data.x,
					y: data.y
				}
			}
			console.log(tempUsers);
			console.log(users);
			setUsers(tempUsers);
		})
	}, [socket]);

	let setup = (p5, canvas) => {
		let canv = p5.createCanvas(800, 800).parent(canvas);
	}

	let draw = (p5) => {
		p5.background("rgb(255, 255, 255)");

		if (p5.keyIsDown(87) || p5.keyIsDown(38)) {
			let tempUsers = users;
			tempUsers[socket.id].y = tempUsers[socket.id].y - 2;
			setUsers(tempUsers);
			socket.emit('send-move', { id: socket.id, room: room, sender: name, x: tempUsers[socket.id].x, y: tempUsers[socket.id].y });
		}
		if (p5.keyIsDown(65) || p5.keyIsDown(37)) {
			let tempUsers = users;
			tempUsers[socket.id].x = tempUsers[socket.id].x - 2;
			setUsers(tempUsers);
			socket.emit('send-move', { id: socket.id, room: room, sender: name, x: tempUsers[socket.id].x, y: tempUsers[socket.id].y });
		}
		if (p5.keyIsDown(83) || p5.keyIsDown(40)) {
			let tempUsers = users;
			tempUsers[socket.id].y = tempUsers[socket.id].y + 2;
			setUsers(tempUsers);
			socket.emit('send-move', {  id: socket.id, room: room, sender: name, x: tempUsers[socket.id].x, y: tempUsers[socket.id].y });
		}
		if (p5.keyIsDown(68) || p5.keyIsDown(39)) {
			let tempUsers = users;
			tempUsers[socket.id].x = tempUsers[socket.id].x + 2;
			setUsers(tempUsers);
			socket.emit('send-move', { id: socket.id, room: room, sender: name, x: tempUsers[socket.id].x, y: tempUsers[socket.id].y });
		}	

		for (let i = 0; i < users.length; i ++) {
			p5.circle(users[i].x, users[i].y, 16);
		}
	}

	return (
		<div className="video">
			<Sketch setup={ setup } draw={ draw } className="canvas" />
		</div>
	)
}

export default VideoRoom
