import React, { useState, useEffect } from 'react'
import './VideoRoom.css'
import Sketch from 'react-p5';

function VideoRoom({ socket, name, room }) {
	const [videoGrid, setVideoGrid] = useState([]);
	const [myVideo, setMyVideo] = useState({});
	const [users, setUsers] = useState({});

	var x = 400;
	var y = 100;

	useEffect(() => {
		socket.on('receive-move', (data) => {
			console.log(data);
			let tempUsers = users;
			if (tempUsers.hasOwnProperty(data.sender)) {
				tempUsers.data.x = data.x;
				tempUsers.data.y = data.y;
			} else {
				tempUsers.data.name = { room: data.room, sender: data.name, x: data.x, y: data.y}
			}
			setUsers(tempUsers);
		})
	}, [socket]);

	let setup = (p5, canvas) => {
		let canv = p5.createCanvas(800, 800).parent(canvas);
	}

	let draw = (p5) => {
		p5.background("rgb(255, 255, 255)");
		p5.circle(x, y, 16);
		if (p5.keyIsDown(87) || p5.keyIsDown(38)) {
			y = y - 2;
			socket.emit('send-move', {room: room, sender: name, x: x, y: y});
		}
		if (p5.keyIsDown(65) || p5.keyIsDown(37)) {
			x = x - 2;
			socket.emit('send-move', {room: room, sender: name, x: x, y: y});
		}
		if (p5.keyIsDown(83) || p5.keyIsDown(40)) {
			y = y + 2;
			socket.emit('send-move', {room: room, sender: name, x: x, y: y});
		}
		if (p5.keyIsDown(68) || p5.keyIsDown(39)) {
			x = x + 2;
			socket.emit('send-move', {room: room, sender: name, x: x, y: y});
		}	
	}

	return (
		<div className="video">
			<Sketch setup={ setup } draw={ draw } className="canvas" />
		</div>
	)
}

export default VideoRoom
