import React, { useState, useEffect } from 'react'
import './VideoRoom.css'
import Sketch from 'react-p5';

function VideoRoom({ socket, name, room }) {
	const [videoGrid, setVideoGrid] = useState([]);
	const [myVideo, setMyVideo] = useState({});
	const [users, setUsers] = useState([]);

	useEffect(() => {
		socket.on('receive-move', (data) => {
			let idx = users.findIndex((user) => user.id === data.id)
			let tempUser = users[idx];
			if (idx === -1) {
				tempUser = {
					id: data.id, 
					room: data.room,
					sender: data.sender,
					x: data.x,
					y: data.y	
				}
				setUsers([...users, tempUser]);
			} else {
				tempUser = {
					id: data.id, 
					room: data.room,
					sender: data.sender,
					x: data.x,
					y: data.y
				}
				let tempUsers = users;
				tempUsers[idx] = tempUser;
				setUsers(tempUsers);
			}
		})
	}, [socket]);

	let setup = (p5, canvas) => {
		let canv = p5.createCanvas(800, 800).parent(canvas);
		let tempUsers = [];
		tempUsers.push({
			id: socket.id,
			room: room,
			sender: name,
			x: 400,
			y: 100
		});
		setUsers(tempUsers);
		console.log(tempUsers);
	}

	let draw = (p5) => {
		p5.background("rgb(255, 255, 255)");

		let idx = users.findIndex((user) => user.id === socket.id)
		if (idx !== -1) {
			if (p5.keyIsDown(87) || p5.keyIsDown(38)) {
				let tempUsers = users;
				tempUsers[idx].y = tempUsers[idx].y - 2;
				setUsers(tempUsers);
				socket.emit('send-move', { id: socket.id, room: room, sender: name, x: tempUsers[idx].x, y: tempUsers[idx].y });
			}
			if (p5.keyIsDown(65) || p5.keyIsDown(37)) {
				let tempUsers = users;
				tempUsers[idx].x = tempUsers[idx].x - 2;
				setUsers(tempUsers);
				socket.emit('send-move', { id: socket.id, room: room, sender: name, x: tempUsers[idx].x, y: tempUsers[idx].y });
			}
			if (p5.keyIsDown(83) || p5.keyIsDown(40)) {
				let tempUsers = users;
				tempUsers[idx].y = tempUsers[idx].y + 2;
				setUsers(tempUsers);
				socket.emit('send-move', {  id: socket.id, room: room, sender: name, x: tempUsers[idx].x, y: tempUsers[idx].y });
			}
			if (p5.keyIsDown(68) || p5.keyIsDown(39)) {
				let tempUsers = users;
				tempUsers[idx].x = tempUsers[idx].x + 2;
				setUsers(tempUsers);
				socket.emit('send-move', { id: socket.id, room: room, sender: name, x: tempUsers[idx].x, y: tempUsers[idx].y });
			}	

			for (let i = 0; i < users.length; i ++) {
				p5.circle(users[i].x, users[i].y, 16);
			}
		}
	}

	return (
		<div className="video">
			<Sketch setup={ setup } draw={ draw } className="canvas" />
		</div>
	)
}

export default VideoRoom
